--  DDL for Procedure TABLE_LOAD_IMAGE
--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "C##SPRINGEDU"."TABLE_LOAD_IMAGE" (
   p_dir        IN   VARCHAR2
 , p_bnum       IN   NUMBER
 , p_file_name  IN   VARCHAR2
)
IS
   l_source   BFILE;
   l_fid      NUMBER;
   l_fdata    BLOB;
   l_ftype    varchar2(100);
   l_fname    varchar2(150);
   l_size     BINARY_INTEGER;
BEGIN
   l_source := BFILENAME (p_dir, p_file_name);
   RETURNING fid, fdata
        INTO l_fid, l_fdata;
   -- lock record
  SELECT fdata
    INTO l_fdata
    FROM board_upload_file
   WHERE fid  = l_fid
     FOR UPDATE;
   -- open the file
   DBMS_LOB.fileopen (l_source, DBMS_LOB.file_readonly);
   -- get length
   l_size := DBMS_LOB.getlength (l_source);
   l_ftype := DBMS_LOB.getcontenttype(l_fdata);
   -- read the file and store in the destination
   DBMS_LOB.loadfromfile (l_fdata, l_source, l_size);

   -- update the blob field with destination
   UPDATE board_upload_file
      SET fdata  = l_fdata, ftype = l_ftype, fname = p_file_name, fsize = l_size
    WHERE fid    = l_fid;

   -- close file
   DBMS_LOB.fileclose (l_source);
   commit;
END;

